import React, { useEffect,useState } from 'react';
// import CompletePayment from './CompletePayment';
import CompletePayment from './CompletePayment';
import { useHistory } from 'react-router-dom';
const UserForm = () => {
      const [formErrors, setFormErrors] = useState({});
      const [isSubmit, setIsSubmit] = useState(false);
      const history = useHistory();
      // const [receiptData, setReceiptData] = useState(null);

      const [formData, setFormData] = useState({
        name: '',
        age: '',
        selectedBatch: '',
      });
      const handleChange = (e) => {
        setFormData({ 
          ...formData, [e.target.name]: e.target.value,
        });
      };
      const generateReceipt = () =>{
          history.push('/receipt-details');
      }
      const handleSubmit = async (e) => {
        e.preventDefault();
        setFormErrors(validate(FormData));
        setIsSubmit(true);

        const response = await fetch('http://localhost:3001/api/submitUser', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        console.log(response)
      };
      
      useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
          console.log(formData);
        }
      }, [formErrors]);
      
      const validate = () => {
        const errors = {}
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
        if (!formData.name) {
          errors.name = "Name is required!";
        }
        const ageValue = parseInt(formData.age, 10);
        if (isNaN(ageValue) || ageValue <= 0) {
          errors.age = "age is required!";
        }else if(ageValue < 18 || ageValue > 65){
          errors.age = "Your age should be greater than 18 or less than 65";
        }
        if (formData.selectedBatch.trim() === '') {
          errors.selectedBatch = "Please select batch as per your convenience!";
        }
        return errors;
      };
  return (
    <>
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" name="name" value={formData.name} onChange={handleChange} />
      </label>
      <br />
      <p>{formErrors.name}</p>
      <label>
        Age:
        <input type="number" name="age" value={formData.age} onChange={handleChange} />
      </label>
      <br />
      <p>{formErrors.age}</p>
      <label>
        Select Batch:
        <select name="selectedBatch" value={formData.selectedBatch} onChange={handleChange}>
          <option value="">Select Batch</option>
          <option value="6-7AM">6-7AM</option>
          <option value="7-8AM">7-8AM</option>
          <option value="8-9AM">8-9AM</option>
          <option value="5-6PM">5-6PM</option>
        </select>
      </label>
      <br />
      <p>{formErrors.selectedBatch}</p>
      <button type="submit">Submit</button>
    </form>
    <br />
    <div className='pay'>
      <a type = "submit" Link to = "/receipt-details">Generate Receipt</a>
    </div>
    </>
  );
};

export default UserForm;
